import pandas as pd
import statistics
import math
import matplotlib.pyplot as plt

n = int(input("Enter the kernel size: "))

df = pd.DataFrame(points_list,columns=["x_coordinates","y_coordinates","z_coordinates"])

x_coordinates_list = df["x_coordinates"].tolist()
y_coordinates_list = df["y_coordinates"].tolist()

#print(len(x_coordinates_list))
#print(len(y_coordinates_list))

if (n-1)%2 == 0:
    x_coordinates_list[0:0] = x_coordinates_list[0:int((n-1)/2)]
    x_coordinates_list[len(x_coordinates_list):len(x_coordinates_list)] = x_coordinates_list[len(x_coordinates_list)-int((n-1)/2):len(x_coordinates_list)]

    y_coordinates_list[0:0] = y_coordinates_list[0:int((n-1)/2)]
    y_coordinates_list[len(y_coordinates_list):len(y_coordinates_list)] = y_coordinates_list[len(y_coordinates_list)-int((n-1)/2):len(y_coordinates_list)]

else:
    x_coordinates_list[0:0] = x_coordinates_list[0:math.ceil((n-1)/2)] 
    x_coordinates_list[len(x_coordinates_list):len(x_coordinates_list)] = x_coordinates_list[(len(x_coordinates_list)-math.floor((n-1)/2)):len(x_coordinates_list)]

    y_coordinates_list[0:0] = y_coordinates_list[0:math.ceil((n-1)/2)]
    y_coordinates_list[len(y_coordinates_list):len(y_coordinates_list)] = y_coordinates_list[(len(y_coordinates_list)-math.floor((n-1)/2)):len(y_coordinates_list)]

print(len(x_coordinates_list))
#print(len(y_coordinates_list))

x_coordinates_median_list = []
y_coordinates_median_list = []

for i in range(len(x_coordinates_list)-(n-1)):
    x_median = statistics.median(x_coordinates_list[(i+0):(i+n)])
    x_coordinates_median_list.append(x_median)
    y_median = statistics.median(y_coordinates_list[(i+0):(i+n)])
    y_coordinates_median_list.append(y_median)

print(len(x_coordinates_median_list))
print(len(y_coordinates_median_list))

filterd_data = pd.DataFrame({"filtered_x_co":x_coordinates_median_list,"filtered_y_co":y_coordinates_median_list})

plt.scatter(df["x_coordinates"],df["y_coordinates"],s=7,c = "#ff0000",marker="o",label = "Actual Data")
plt.scatter(filterd_data["filtered_x_co"],filterd_data["filtered_y_co"],s=7,c = "#0000ff",marker="s",label = "Filtered Data")
plt.legend()
plt.show()